mapper.select:
select a.c_client_id, sum(a.power) as power, c.name as groupname, d.client_code, e.name, e.location, e.lamp_pole_num, f.name as wgname, b.name as clname from b_client_power_daily_report a,c_term c,c_client d,c_client_attr1 e,c_gateway_attr1 f,c_client_term g,c_client_gateway h,b_ctrl_strategy b where a.c_client_id=g.c_client_id and g.c_term_id=c.id and a.c_client_id=d.id and a.c_client_id=e.c_client_id and a.c_client_id=h.c_client_id and h.c_gateway_id=f.c_gateway_id and c.b_ctrl_strategy_id=b.id and e.sys_user_id in ${pd.userids}
and c.name LIKE CONCAT(CONCAT('%', #{pd.groupname}),'%')
and b.name LIKE CONCAT(CONCAT('%', #{pd.strategy}),'%')
and a.day >= #{pd.starttime}
and a.day <= #{pd.endtime}
and a.tdate LIKE CONCAT(CONCAT('%', #{pd.starttime}),'%')
group by a.c_client_id order by c.name

select a.c_client_id, sum(a.power) as power, c.name as groupname, d.client_code, e.name, e.location, e.lamp_pole_num, f.name as wgname, b.name as clname from b_client_power_monthly_report a,c_term c,c_client d,c_client_attr1 e,c_gateway_attr1 f,c_client_term g,c_client_gateway h,b_ctrl_strategy b where a.c_client_id=g.c_client_id and g.c_term_id=c.id and a.c_client_id=d.id and a.c_client_id=e.c_client_id and a.c_client_id=h.c_client_id and h.c_gateway_id=f.c_gateway_id and c.b_ctrl_strategy_id=b.id and e.sys_user_id in ${pd.userids}
and c.name LIKE CONCAT(CONCAT('%', #{pd.groupname}),'%')
and b.name LIKE CONCAT(CONCAT('%', #{pd.strategy}),'%')
and a.month >= #{pd.starttime}
and a.month <= #{pd.endtime}
and a.month LIKE CONCAT(CONCAT('%', #{pd.starttime}),'%')
group by a.c_client_id order by c.name
