mapper.select:
select count(*) as equipmentcnt from ( SELECT c_client.id FROM c_client LEFT JOIN c_client_attr1 ON c_client.id = c_client_attr1.c_client_id WHERE 1=1 AND c_client_attr1.sys_user_id in ${pd.userids}
union all
SELECT c_sensor.id FROM c_sensor LEFT JOIN c_sensor_attr1 ON c_sensor.id = c_sensor_attr1.c_sensor_id WHERE 1=1 AND c_sensor_attr1.sys_user_id in ${pd.userids}
union all
SELECT c_gateway.id FROM c_gateway LEFT JOIN c_gateway_attr1 ON c_gateway.id = c_gateway_attr1.c_gateway_id WHERE 1=1 AND c_gateway_attr1.sys_user_id in ${pd.userids}
union all
SELECT c_breaker.id FROM c_breaker LEFT JOIN c_breaker_attr1 ON c_breaker.id = c_breaker_attr1.c_breaker_id WHERE 1=1 AND c_breaker_attr1.sys_user_id in ${pd.userids}
)b
