mapper.select:
  		select bp.c_gateway_id as id ,powerup ,powerdown, tdate
		from b_powerup bp
		where bp.c_gateway_id=#{id}

  		select cca.`name`
		from c_client_gateway ccg ,c_client_attr1 cca
		where cca.c_client_id=ccg.c_client_id and ccg.c_gateway_id=#{id}

select cc.id,cca.name,cca.lamp_pole_num as lamppolenum ,cca.location, CASE WHEN partMap.c_client_id IS NOT NULL then partMap.external_coordinate ELSE cca.coordinate end coordinate , ct.name as termname,ccus.brightness ,ccty.name as typename , ct.id as termid ,ccty.id as typeid ,bs.`name` as status,"灯" as aliastypename, CASE WHEN partMap.c_client_id IS NOT NULL THEN 1 ELSE 0 END as partMaplinetCnt, partMap.id as partMap_Id from c_client cc INNER join c_client_attr1 cca ON cc.id = cca.c_client_id INNER JOIN c_client_term cct ON cc.id = cct.c_client_id INNER JOIN c_term ct ON cct.c_term_id = ct.id INNER JOIN c_client_upload_status ccus ON ccus.c_client_id = cca.id INNER JOIN b_status bs ON cca.staus=bs.`value` and bs.b_status_main_id=3 INNER JOIN c_client_type ccty ON ccty.id=cca.c_client_type_id LEFT JOIN (select cdl.c_client_id,external_coordinate,cpp.id,cpp.c_termid from c_partmap cpp inner join c_partmap_detail cdl where cdl.id=cpp.id and c_client_type_id='1' ) partMap ON partMap.c_client_id= cca.c_client_id and partmap.c_termid=cct.c_term_id where cca.coordinate like '%.%_,__.%' and cct.c_client_id in
#{item}
ORDER BY cct.c_client_id ASC

select cg.id as id ,cga.name as name ,cga.coordinate as coordinate, cga.location as location ,cga.lamp_pole_num as lamppolenum ,cgt.NAME_CH as typename ,-999 as termid ,bs.`name` as status ,"网关/断路器组" as termname , cgt.NAME_CH as aliastypename,-999 as termid from c_gateway cg ,c_gateway_attr1 cga ,c_gateway_type cgt ,b_status bs where cg.id=cga.c_gateway_id and cga.c_gateway_type_id=cgt.id and bs.b_status_main_id=3 and bs.`value`=cga.staus and cga.coordinate like '%.%_,__.%' and cga.c_gateway_id in
#{item}
ORDER BY cga.c_gateway_id ASC

mapper.update:
  		update c_client_upload_status cca set cca.brightness=100  where cca.c_client_id 
  		in
		
            #{item}

  		update c_client_upload_status cca set cca.brightness=0  where cca.c_client_id   
  		in
		
            #{item}

  		update c_client_upload_status cca set cca.brightness=#{bright}  where cca.c_client_id   
  		in
		
                 #{item}
