mapper.select:
SELECT * FROM(
SELECT '1' as device_type, b_client_repair.id, b_client_fault.fault_no, b_client_fault_id as fault_id, b_client_repair.register, b_client_repair.repairman, a.name as repairman_name, b.name as register_name, b_client_repair.tdate, operate FROM b_client_repair LEFT JOIN b_client_fault ON b_client_fault.id = b_client_repair.b_client_fault_id LEFT JOIN c_client_attr1 ON c_client_attr1.c_client_id = b_client_fault.c_client_id LEFT JOIN sys_user a ON a.user_id = b_client_repair.repairman LEFT JOIN sys_user b ON b.user_id = b_client_repair.register WHERE c_client_attr1.sys_user_id in ${pd.userids}
AND b_client_fault.fault_no LIKE CONCAT(CONCAT('%', #{pd.fault_no}), '%')
AND DATE_FORMAT(b_client_repair.tdate,'%Y-%m-%d') >= #{pd.Start}
AND DATE_FORMAT(b_client_repair.tdate,'%Y-%m-%d') <= #{pd.End}
AND a.name LIKE CONCAT(CONCAT('%', #{pd.maintainer}), '%')
AND b.name LIKE CONCAT(CONCAT('%', #{pd.registrant}), '%')
GROUP BY b_client_repair.id
UNION ALL
SELECT '2' as device_type, b_gateway_repair.id, b_gateway_fault.fault_no, b_gateway_fault_id as fault_id, b_gateway_repair.register, b_gateway_repair.repairman, a.name as repairman_name, b.name as register_name, b_gateway_repair.tdate, operate FROM b_gateway_repair LEFT JOIN b_gateway_fault ON b_gateway_fault.id = b_gateway_repair.b_gateway_fault_id LEFT JOIN c_gateway_attr1 ON c_gateway_attr1.c_gateway_id = b_gateway_fault.c_gateway_id LEFT JOIN sys_user a ON a.user_id = b_gateway_repair.repairman LEFT JOIN sys_user b ON b.user_id = b_gateway_repair.register WHERE c_gateway_attr1.sys_user_id in ${pd.userids}
AND b_gateway_fault.fault_no LIKE CONCAT(CONCAT('%', #{pd.fault_no}), '%')
AND DATE_FORMAT(b_gateway_repair.tdate,'%Y-%m-%d') >= #{pd.Start}
AND DATE_FORMAT(b_gateway_repair.tdate,'%Y-%m-%d') <= #{pd.End}
AND a.name LIKE CONCAT(CONCAT('%', #{pd.maintainer}), '%')
AND b.name LIKE CONCAT(CONCAT('%', #{pd.registrant}), '%')
GROUP BY b_gateway_repair.id
) AS allrecord ORDER BY tdate
