mapper.select:
SELECT a.id AS id, a.fault_no, a.type, e.`name` AS status_name, a.tdate AS startime, a.comment, b.register, b.repairman, b.tdate,
c.name, c.location, c.lamp_pole_num AS polenumber, c.c_client_id, d.client_code AS code FROM b_client_fault AS a LEFT JOIN b_client_repair AS b ON a.id = b.b_client_fault_id LEFT JOIN c_client_attr1 AS c ON a.c_client_id = c.c_client_id LEFT JOIN c_client AS d ON c.c_client_id = d.id LEFT JOIN b_status AS e ON e.b_status_main_id = '4' AND a.type = e.value WHERE a.status='1'
AND c.sys_user_id in ${pd.userids}
AND a.fault_no LIKE CONCAT(CONCAT('%', #{pd.fault_no}), '%')
AND d.client_code LIKE CONCAT(CONCAT('%', #{pd.code}), '%')
AND c.name LIKE CONCAT(CONCAT('%', #{pd.name}), '%')
AND c.location LIKE CONCAT(CONCAT('%', #{pd.location}), '%')
AND DATE_FORMAT(a.tdate,'%Y-%m-%d') >= #{pd.Start}
AND DATE_FORMAT(a.tdate,'%Y-%m-%d') <= #{pd.End}
GROUP BY a.id

mapper.insert:
insert into b_client_repair ( b_client_fault_id,
register, repairman, tdate, operate
) values ( #{id},
#{register}, #{repairman}, #{tdate}, #{operate}
)
