mapper.select:
SELECT a.id AS id, a.type, a.tdate AS startime, a.comment, b.register, b.repairman, b.tdate, b.result, b.explain, c.name, c.location, c.lamp_pole_num AS polenumber, d.client_code AS code FROM b_client_fault AS a LEFT JOIN b_client_repair AS b ON a.id = b.b_client_fault_id LEFT JOIN c_client_attr1 AS c ON a.c_client_id = c.id LEFT JOIN c_client AS d ON c.c_client_id = d.id WHERE 1=1
AND c.sys_user_id in ${pd.userids}
AND c.name LIKE CONCAT(CONCAT('%', #{pd.name}), '%')
AND c.location LIKE CONCAT(CONCAT('%', #{pd.location}), '%')
AND d.client_code LIKE CONCAT(CONCAT('%', #{pd.code}), '%')
AND g.name LIKE CONCAT(CONCAT('%', #{pd.cname}), '%')
AND a.tdate > #{pd.Start}
AND a.tdate < #{pd.End}

SELECT a.id AS id, a.type, a.tdate AS startime, a.comment, b.register, b.repairman, b.tdate, b.result, b.explain, c.name, c.location, c.lamp_pole_num AS polenumber, d.gateway_code AS code FROM b_gateway_fault AS a LEFT JOIN b_gateway_repair AS b ON a.id = b.b_gateway_fault_id LEFT JOIN c_gateway_attr1 AS c ON a.c_gateway_id = c.c_gateway_id LEFT JOIN c_gateway AS d ON c.c_gateway_id = d.id WHERE 1=1
AND c.sys_user_id in ${pd.userids}
AND c.name LIKE CONCAT(CONCAT('%', #{pd.name}), '%')
AND d.gateway_code LIKE CONCAT(CONCAT('%', #{pd.code}), '%')
AND c.location LIKE CONCAT(CONCAT('%', #{pd.location}), '%')
AND a.tdate > #{pd.Start}
AND a.tdate < #{pd.End}

SELECT b.id, b.client_code AS number, c.`comment`, c.`name`, c.staus AS status, c.lamp_pole_num AS polenumber FROM c_client_gateway AS a LEFT JOIN c_client AS b ON a.c_client_id = b.id LEFT JOIN c_client_attr1 AS c ON b.id = c.c_client_id WHERE a.c_gateway_id = #{pd.id}
AND c.name LIKE CONCAT(CONCAT('%', #{pd.name}), '%')
AND b.gateway_code LIKE CONCAT(CONCAT('%', #{pd.code}), '%')

mapper.delete:
DELETE 
FROM c_client_gateway
WHERE c_client_gateway.c_gateway_id = #{gateway}
AND c_client_gateway.c_client_id = #{client}
