mapper.select:
SELECT a.id,a.iccid,a.phone_number AS mobile,a.type,b.`value` AS `status`,b.`name` AS statusname,a.money,a.`comment` FROM c_sim AS a LEFT JOIN b_status AS b ON a.`status` = b.`value` LEFT JOIN b_status_main AS c ON b.b_status_main_id = c.id WHERE c.id = 2
AND a.iccid LIKE CONCAT(CONCAT('%', #{pd.iccid}),'%')
AND a.phone_number LIKE CONCAT(CONCAT('%', #{pd.mobile}), '%')
AND a.type = #{pd.type, jdbcType=INTEGER}
AND b.value = #{pd.status, jdbcType=INTEGER}

mapper.update:
UPDATE c_client AS a JOIN c_client_attr1 AS b ON a.id = b.c_client_id JOIN c_client_attr2 AS c ON a.id = c.c_client_id SET a.client_code =#{number}, b.name =#{name}, b.password =#{password}, b.c_client_type_id =#{typeid}, b.location =#{location}, b.coordinate =#{coordinate}, b.comment =#{comment}, b.staus =#{status}, b.lamp_pole_num =#{polenumber},
c.c_lamp_pole_type_id =#{pole}
WHERE a.id =#{id}

