SELECT a.`comment`, DATE_FORMAT(a.tdate,'%Y-%m-%d %H:%i:%S') AS time, b.NAME AS `name`, c.`name` AS type
FROM `b_user_log` AS a
LEFT JOIN sys_user AS b ON a.sys_user_id = b.USER_ID
LEFT JOIN b_log_type AS c ON a.b_log_type_id = c.id
WHERE 1=1

	AND a.sys_user_id = #{pd.userid}


	AND c.id = #{pd.id}


	AND a.tdate > #{pd.lastStart}


	AND a.tdate < #{pd.lastEnd}


	AND a.b_log_type_id = #{pd.type}


	AND b.NAME LIKE CONCAT(CONCAT('%', #{pd.username}),'%')

ORDER BY a.id DESC

SELECT a.`comment`, DATE_FORMAT(a.tdate,'%Y-%m-%d %H:%i:%S') AS time, b.NAME AS `name`, c.`name` AS type
FROM `b_user_log` AS a
LEFT JOIN sys_user AS b ON a.sys_user_id = b.USER_ID
LEFT JOIN b_log_type AS c ON a.b_log_type_id = c.id
WHERE 1=1

	AND a.sys_user_id = #{userid}


	AND c.id = #{id}


	AND a.tdate > #{lastStart}


	AND a.tdate < #{lastEnd}


	AND a.b_log_type_id = #{type}


	AND b.NAME LIKE CONCAT(CONCAT('%', #{username}),'%')

ORDER BY a.id DESC

SELECT g.`NAME` AS name, DATE_FORMAT(f.tdate,'%Y-%m-%d %H:%i:%S') AS operate_time, h.`name` AS cmd_type, c.`name` AS gateway, b.`name` AS device, a.cmd AS `comment`, d.`name` AS `status`, DATE_FORMAT(a.tdate,'%Y-%m-%d %H:%i:%S') AS feedback_time FROM b_client_log AS a LEFT JOIN c_client_attr1 AS b ON a.c_client_id = b.c_client_id LEFT JOIN c_gateway_attr1 AS c ON a.c_gateway_id = c.c_gateway_id LEFT JOIN b_status AS d ON a.`status` = d.`value` LEFT JOIN b_status_main AS e ON d.b_status_main_id = e.id LEFT JOIN b_user_log AS f ON a.b_uesr_log_id = f.id LEFT JOIN sys_user AS g ON f.sys_user_id = g.USER_ID LEFT JOIN b_cmd_type AS h ON a.b_cmd_type_id = h.id WHERE e.id = 5
AND f.sys_user_id = #{pd.userid}
AND h.id = #{pd.type}
AND f.tdate > #{pd.starttime}
AND f.tdate < #{pd.endtime}
AND g.NAME LIKE CONCAT(CONCAT('%', #{pd.username}),'%')
ORDER BY f.tdate DESC

SELECT g.`NAME` AS name, DATE_FORMAT(f.tdate,'%Y-%m-%d %H:%i:%S') AS operate_time, h.`name` AS cmd_type, c.`name` AS gateway, b.`name` AS device, a.cmd AS `comment`, d.`name` AS `status`, DATE_FORMAT(a.tdate,'%Y-%m-%d %H:%i:%S') AS feedback_time FROM b_client_log AS a LEFT JOIN c_client_attr1 AS b ON a.c_client_id = b.c_client_id LEFT JOIN c_gateway_attr1 AS c ON a.c_gateway_id = c.c_gateway_id LEFT JOIN b_status AS d ON a.`status` = d.`value` LEFT JOIN b_status_main AS e ON d.b_status_main_id = e.id LEFT JOIN b_user_log AS f ON a.b_uesr_log_id = f.id LEFT JOIN sys_user AS g ON f.sys_user_id = g.USER_ID LEFT JOIN b_cmd_type AS h ON a.b_cmd_type_id = h.id WHERE e.id = 5
AND f.sys_user_id = #{userid}
AND h.id = #{type}
AND f.tdate > #{starttime}
AND f.tdate < #{endtime}
AND g.NAME LIKE CONCAT(CONCAT('%', #{username}),'%')
ORDER BY f.tdate DESC

SELECT c_gateway_id AS gateway 
FROM c_client_gateway
WHERE c_client_id = #{client}

SELECT REPLACE(GROUP_CONCAT(b.client_code), ',','、') AS cmd
FROM c_client_gateway AS a
LEFT JOIN c_client AS b ON a.c_client_id = b.id
WHERE a.c_gateway_id = #{gateway}
AND a.c_client_id IN ${ids}

SELECT node AS param1, node2 AS param2
FROM c_client_gateway AS a
LEFT JOIN c_client_attr1 AS b ON a.c_client_id = b.id
WHERE a.c_gateway_id = #{gateway}
AND a.c_client_id IN ${ids}

SELECT REPLACE(GROUP_CONCAT(client_code), ',','、') AS cmd
FROM c_client
WHERE id in ${ids}
